import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Register = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showPopup, setShowPopup] = useState(false);
  const [strength, setStrength] = useState("");

  const navigate = useNavigate();

  const handlePasswordChange = (e) => {
    const val = e.target.value;
    setPassword(val);

    if (val.length > 8 && /[A-Z]/.test(val) && /[0-9]/.test(val)) {
      setStrength("Strong");
    } else if (val.length >= 6) {
      setStrength("Medium");
    } else {
      setStrength("Weak");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (password.length < 6 || password !== confirmPassword) {
      alert("Validation failed");
      return;
    }

    try {
      await axios.post("http://localhost:5000/api/auth/register", {
        name,
        email,
        password,
      });
      setShowPopup(true);
    } catch {
      alert("Registration failed");
    }
  };

  const closePopup = () => {
    setShowPopup(false);
    navigate("/login"); // âœ… redirect after signup
  };

  return (
    <>
      <div className="browser-frame">
        <div className="browser-header">
          <span></span><span></span><span></span>
        </div>

        <form onSubmit={handleSubmit}>
          <h2>Signup Form</h2>

          <label>Username:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />

          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />

          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={handlePasswordChange}
            required
          />
          <div className="strength">Strength: {strength}</div>

          <label>Confirm Password:</label>
          <input
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />

          <button type="submit">Sign Up</button>
        </form>
      </div>

      {showPopup && (
        <div id="popup" style={{ display: "flex" }}>
          <div className="popup-content">
            <div className="popup-header">
              <span></span><span></span><span></span>
            </div>
            <div className="popup-body">
              <h3>ðŸŽ‰ Signup Successful!</h3>
              <button onClick={closePopup}>Go to Login</button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Register;
