import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function Cart() {
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);

  const user = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    // ðŸ” If NOT logged in â†’ go to login
    if (!user) {
      navigate("/login");
      return;
    }

    // âœ… If logged in â†’ fetch cart
    const fetchCart = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/cart", {
          headers: {
            Authorization: `Bearer ${user.token}`,
          },
        });
        setCart(res.data.products || []);
      } catch (error) {
        console.error("Error fetching cart:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchCart();
  }, [navigate, user]);

  if (!user) return null; // prevents flicker

  if (loading) {
    return <p style={{ padding: "20px" }}>Loading cart...</p>;
  }

  return (
    <div style={{ padding: "20px" }}>
      <h3>Your Cart</h3>

      {cart.length === 0 ? (
        <p>Cart is empty</p>
      ) : (
        cart.map((item, index) => {
          if (!item.product) return null;

          return (
            <div
              key={index}
              style={{
                border: "1px solid #ddd",
                padding: "10px",
                marginBottom: "8px",
                borderRadius: "5px",
              }}
            >
              <p>
                <strong>{item.product.name}</strong> Ã— {item.quantity}
              </p>
              <p>Total: â‚¹{item.product.price * item.quantity}</p>
            </div>
          );
        })
      )}
    </div>
  );
}
