import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [generalError, setGeneralError] = useState("");
  const navigate = useNavigate();

  const submit = async (e) => {
    e.preventDefault();

    setEmailError("");
    setPasswordError("");
    setGeneralError("");

    let valid = true;

    if (!email.trim()) {
      setEmailError("Email is required.");
      valid = false;
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      setEmailError("Enter a valid email.");
      valid = false;
    }

    if (!password.trim()) {
      setPasswordError("Password is required.");
      valid = false;
    }

    if (!valid) return;

    try {
      const response = await axios.post("http://localhost:5000/api/auth/login", {
        email,
        password,
      });

      // 1️⃣ Show success alert
      alert("Login successful");

      // 2️⃣ Optionally save user info/token in localStorage
      localStorage.setItem("user", JSON.stringify(response.data));

      // 3️⃣ Redirect automatically to dashboard or home
      navigate("/home"); // change "/home" to your desired page

    } catch (error) {
      // If backend sends a message
      if (error.response && error.response.data.message) {
        setGeneralError(error.response.data.message);
      } else {
        setGeneralError("Invalid email or password");
      }
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={submit}>
        <h3>Login</h3>

        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <div className="error">{emailError}</div>

        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <div className="error">{passwordError}</div>

        <div className="error">{generalError}</div>

        <button type="submit" className="login-btn">
          Login
        </button>

        <p className="signup-text">
          If not already an account, signup
        </p>

        <button
          type="button"
          className="signup-btn"
          onClick={() => navigate("/signup")}
        >
          Signup
        </button>
      </form>
    </div>
  );
}
